mapper.update:
UPDATE mst_users
SET last_login_at = CONVERT_TZ(NOW(), '+00:00', '+07:00')
WHERE id = #{id}

UPDATE mst_users
SET remember_token = #{token}
WHERE id = #{id}

UPDATE mst_users
SET last_login_ip = #{ip}
WHERE id = #{id}

mapper.select:
SELECT * FROM mst_users
WHERE is_delete = 0

    AND name LIKE CONCAT('%', #{name}, '%')


    AND email LIKE CONCAT('%', #{email}, '%')


    AND group_role = #{group}


    AND is_active = #{status}

ORDER BY id DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM mst_users WHERE is_delete = 0
AND name LIKE CONCAT('%', #{name}, '%')
AND email LIKE CONCAT('%', #{email}, '%')
AND group_role = #{group}
AND is_active = #{status}
ORDER BY id DESC

mapper.update:
UPDATE mst_users
SET is_active = #{is_active}
WHERE id = #{id}

UPDATE mst_users
SET is_delete = 1
WHERE id = #{id}

UPDATE mst_users
SET name = #{name}, email = #{email}, group_role = #{groupRole}, password = #{password}
WHERE customer_id = #{customer_id}
